if Country.all.blank?
	p "Country: I am blank!!! Initializing..."
	country = Country.create :name => :atlantic, :serial_id => 1
	if Rails.env.test?
		(1..5).each do |i|
			AreaMap.create :x => i, :y => i, :blocked => i == 3, :country_id => country.id
		end
	else
		blocked_info = ""
		(0..49).each do |i|
			(0..49).each do |j|
				blk = blocked_info[i + j * 50] == '0' ? false : true
				AreaMap.create :x => i, :y => j, :blocked => blk, :country_id => country.id
			end
		end
	end
	
	p "OK"
else
	p "AreaMap: I'm full!"
end